      "source": "import { Alert, AlertDescription, AlertTitle } from \"@/components/ui/cubby-ui/alert\";\n\nexport default function AlertDemo() {\n  return (\n    <Alert>\n      <AlertTitle>Heads up!</AlertTitle>\n      <AlertDescription>\n        You can add components and dependencies to your app using the cli.\n      </AlertDescription>\n    </Alert>\n  );\n}"
