  "alert-dialog": [
    {
      "title": "Basic",
      "importPath": "alert-dialog-basic",
      "source": "import {\n  AlertDialog,\n  AlertDialogClose,\n  AlertDialogContent,\n  AlertDialogDescription,\n  AlertDialogFooter,\n  AlertDialogHeader,\n  AlertDialogTitle,\n  AlertDialogTrigger,\n} from \"@/components/ui/cubby-ui/alert-dialog\";\nimport { Button } from \"@/components/ui/cubby-ui/button\";\n\nexport default function AlertDialogBasic() {\n  return (\n    <AlertDialog>\n      <AlertDialogTrigger render={<Button variant=\"outline\" />}>\n        Delete Account\n      </AlertDialogTrigger>\n      <AlertDialogContent>\n        <AlertDialogHeader>\n          <AlertDialogTitle>Are you absolutely sure?</AlertDialogTitle>\n          <AlertDialogDescription>\n            This action cannot be undone. This will permanently delete your\n            account and remove your data from our servers.\n          </AlertDialogDescription>\n        </AlertDialogHeader>\n        <AlertDialogFooter>\n          <AlertDialogClose\n            render={<Button variant=\"outline\">Cancel</Button>}\n          />\n          <AlertDialogClose\n            render={<Button variant=\"destructive\">Continue</Button>}\n          />\n        </AlertDialogFooter>\n      </AlertDialogContent>\n    </AlertDialog>\n  );\n}\n"
    },
    {
      "title": "Controlled",
      "importPath": "alert-dialog-controlled",
      "source": "\"use client\";\n\nimport * as React from \"react\";\nimport {\n  AlertDialog,\n  AlertDialogClose,\n  AlertDialogContent,\n  AlertDialogDescription,\n  AlertDialogFooter,\n  AlertDialogHeader,\n  AlertDialogTitle,\n} from \"@/components/ui/cubby-ui/alert-dialog\";\nimport { Button } from \"@/components/ui/cubby-ui/button\";\n\nexport default function AlertDialogControlled() {\n  const [open, setOpen] = React.useState(false);\n\n  return (\n    <>\n      <Button onClick={() => setOpen(true)}>Open</Button>\n\n      <AlertDialog open={open} onOpenChange={setOpen}>\n        <AlertDialogContent>\n          <AlertDialogHeader>\n            <AlertDialogTitle>Controlled Alert Dialog</AlertDialogTitle>\n            <AlertDialogDescription>\n              This alert dialog is controlled by external state. You can open it\n              programmatically.\n            </AlertDialogDescription>\n          </AlertDialogHeader>\n          <AlertDialogFooter>\n            <AlertDialogClose\n              render={<Button variant=\"outline\">Cancel</Button>}\n            />\n            <AlertDialogClose\n              render={<Button variant=\"destructive\">Confirm</Button>}\n            />\n          </AlertDialogFooter>\n        </AlertDialogContent>\n      </AlertDialog>\n    </>\n  );\n}\n"
    },
    {
      "title": "Nested",
      "importPath": "alert-dialog-nested",
      "source": "\"use client\";\n\nimport * as React from \"react\";\nimport {\n  AlertDialog,\n  AlertDialogClose,\n  AlertDialogContent,\n  AlertDialogDescription,\n  AlertDialogFooter,\n  AlertDialogHeader,\n  AlertDialogTitle,\n  AlertDialogTrigger,\n} from \"@/components/ui/cubby-ui/alert-dialog\";\nimport { Button } from \"@/components/ui/cubby-ui/button\";\n\nexport default function AlertDialogNested() {\n  const [open, setOpen] = React.useState(false);\n  const [nestedOpen, setNestedOpen] = React.useState(false);\n\n  const handleConfirm = () => {\n    setNestedOpen(false);\n    setOpen(false);\n  };\n\n  return (\n    <AlertDialog open={open} onOpenChange={setOpen}>\n      <AlertDialogTrigger render={<Button variant=\"outline\" />}>\n        Delete Account\n      </AlertDialogTrigger>\n      <AlertDialogContent>\n        <AlertDialogHeader>\n          <AlertDialogTitle>Delete Account</AlertDialogTitle>\n          <AlertDialogDescription>\n            Are you sure you want to delete your account? This action cannot be\n            undone.\n          </AlertDialogDescription>\n        </AlertDialogHeader>\n        <AlertDialogFooter>\n          <AlertDialogClose\n            render={<Button variant=\"outline\">Cancel</Button>}\n          />\n\n          <AlertDialog open={nestedOpen} onOpenChange={setNestedOpen}>\n            <AlertDialogTrigger render={<Button variant=\"destructive\" />}>\n              Delete\n            </AlertDialogTrigger>\n            <AlertDialogContent>\n              <AlertDialogHeader>\n                <AlertDialogTitle>Are you absolutely certain?</AlertDialogTitle>\n                <AlertDialogDescription>\n                  This will permanently delete your account and all associated\n                  data. Type \"DELETE\" to confirm.\n                </AlertDialogDescription>\n              </AlertDialogHeader>\n              <AlertDialogFooter>\n                <AlertDialogClose\n                  render={<Button variant=\"outline\">Go Back</Button>}\n                />\n                <Button variant=\"destructive\" onClick={handleConfirm}>\n                  I Understand, Delete My Account\n                </Button>\n              </AlertDialogFooter>\n            </AlertDialogContent>\n          </AlertDialog>\n        </AlertDialogFooter>\n      </AlertDialogContent>\n    </AlertDialog>\n  );\n}\n"
    }
  ],
