    {
      "title": "With Segments",
      "importPath": "meter-with-segments",
      "source": "import {\n  MeterRoot,\n  MeterTrack,\n  MeterIndicator,\n  MeterLabel,\n  MeterValue,\n} from \"@/components/ui/cubby-ui/meter\";\n\nexport default function MeterWithSegments() {\n  return (\n    <div className=\"w-full max-w-[300px] space-y-6\">\n      {/* Step progress indicator */}\n      <MeterRoot value={4} max={5}>\n        <div className=\"flex items-center justify-between gap-2\">\n          <MeterLabel>Setup Progress</MeterLabel>\n          <MeterValue>\n            {(formattedValue, value) => `${value} of 5 steps`}\n          </MeterValue>\n        </div>\n        <MeterTrack segments={5}>\n          <MeterIndicator />\n        </MeterTrack>\n      </MeterRoot>\n\n      {/* Segments with thresholds */}\n      <MeterRoot value={35} low={25} high={75}>\n        <div className=\"flex items-center justify-between gap-2\">\n          <MeterLabel>Segmented with thresholds</MeterLabel>\n          <MeterValue />\n        </div>\n        <MeterTrack segments={4}>\n          <MeterIndicator />\n        </MeterTrack>\n        {/* <div className=\"text-muted-foreground mt-1 flex justify-between text-xs\">\n          <span>0</span>\n          <span>25</span>\n          <span>50</span>\n          <span>75</span>\n          <span>100</span>\n        </div> */}\n      </MeterRoot>\n\n      {/* Visual battery indicator */}\n      <MeterRoot value={45} low={20} optimumMin={75}>\n        <MeterLabel>Battery Level </MeterLabel>\n        <MeterTrack segments={10} className=\"h-6!\">\n          <MeterIndicator />\n        </MeterTrack>\n        <MeterValue className=\"text-right\">\n          {(formattedValue, value) => `${value}% charged`}\n        </MeterValue>\n      </MeterRoot>\n    </div>\n  );\n}\n"
    },
    {
      "title": "With Thresholds",
      "importPath": "meter-with-thresholds",
      "source": "import {\n  MeterRoot,\n  MeterTrack,\n  MeterIndicator,\n  MeterLabel,\n  MeterValue,\n} from \"@/components/ui/cubby-ui/meter\";\n\nexport default function MeterWithThresholds() {\n  return (\n    <div className=\"w-full max-w-[300px] space-y-6\">\n      {/* Higher is better - three tiers (battery level) */}\n      <div className=\"space-y-2\">\n        <p className=\"text-muted-foreground text-xs font-medium\">\n          Battery Level (higher is better)\n        </p>\n        <MeterRoot value={10} low={15} optimumMin={75}>\n          <div className=\"flex items-center justify-between gap-2\">\n            <MeterLabel>Battery</MeterLabel>\n            <MeterValue>{(_formattedValue, value) => `${value}%`}</MeterValue>\n          </div>\n          <MeterTrack>\n            <MeterIndicator />\n          </MeterTrack>\n        </MeterRoot>\n\n        <MeterRoot value={50} low={15} optimumMin={75}>\n          <div className=\"flex items-center justify-between gap-2\">\n            <MeterLabel>Battery</MeterLabel>\n            <MeterValue>{(_formattedValue, value) => `${value}%`}</MeterValue>\n          </div>\n          <MeterTrack>\n            <MeterIndicator />\n          </MeterTrack>\n        </MeterRoot>\n\n        <MeterRoot value={85} low={15} optimumMin={75}>\n          <div className=\"flex items-center justify-between gap-2\">\n            <MeterLabel>Battery</MeterLabel>\n            <MeterValue>{(_formattedValue, value) => `${value}%`}</MeterValue>\n          </div>\n          <MeterTrack>\n            <MeterIndicator />\n          </MeterTrack>\n        </MeterRoot>\n      </div>\n\n      {/* Lower is better - three tiers (CPU temperature) */}\n      <div className=\"space-y-2\">\n        <p className=\"text-muted-foreground text-xs font-medium\">\n          CPU Temperature (lower is better)\n        </p>\n        <MeterRoot value={45} optimumMax={60} high={80} min={0} max={100}>\n          <div className=\"flex items-center justify-between gap-2\">\n            <MeterLabel>CPU Temp</MeterLabel>\n            <MeterValue>{(_formattedValue, value) => `${value}C`}</MeterValue>\n          </div>\n          <MeterTrack>\n            <MeterIndicator />\n          </MeterTrack>\n        </MeterRoot>\n\n        <MeterRoot value={70} optimumMax={60} high={80} min={0} max={100}>\n          <div className=\"flex items-center justify-between gap-2\">\n            <MeterLabel>CPU Temp</MeterLabel>\n            <MeterValue>{(_formattedValue, value) => `${value}C`}</MeterValue>\n          </div>\n          <MeterTrack>\n            <MeterIndicator />\n          </MeterTrack>\n        </MeterRoot>\n\n        <MeterRoot value={90} optimumMax={60} high={80} min={0} max={100}>\n          <div className=\"flex items-center justify-between gap-2\">\n            <MeterLabel>CPU Temp</MeterLabel>\n            <MeterValue>{(_formattedValue, value) => `${value}C`}</MeterValue>\n          </div>\n          <MeterTrack>\n            <MeterIndicator />\n          </MeterTrack>\n        </MeterRoot>\n      </div>\n\n      {/* Middle is best - two tiers (body temperature) */}\n      <div className=\"space-y-2\">\n        <p className=\"text-muted-foreground text-xs font-medium\">\n          Body Temperature (middle range is safe)\n        </p>\n        <MeterRoot value={35} low={36} high={38} min={34} max={42}>\n          <div className=\"flex items-center justify-between gap-2\">\n            <MeterLabel>Body Temp</MeterLabel>\n            <MeterValue>{(_formattedValue, value) => `${value}C`}</MeterValue>\n          </div>\n          <MeterTrack>\n            <MeterIndicator />\n          </MeterTrack>\n        </MeterRoot>\n\n        <MeterRoot value={37} low={36} high={38} min={34} max={42}>\n          <div className=\"flex items-center justify-between gap-2\">\n            <MeterLabel>Body Temp</MeterLabel>\n            <MeterValue>{(_formattedValue, value) => `${value}C`}</MeterValue>\n          </div>\n          <MeterTrack>\n            <MeterIndicator />\n          </MeterTrack>\n        </MeterRoot>\n\n        <MeterRoot value={40} low={36} high={38} min={34} max={42}>\n          <div className=\"flex items-center justify-between gap-2\">\n            <MeterLabel>Body Temp</MeterLabel>\n            <MeterValue>{(_formattedValue, value) => `${value}C`}</MeterValue>\n          </div>\n          <MeterTrack>\n            <MeterIndicator />\n          </MeterTrack>\n        </MeterRoot>\n      </div>\n    </div>\n  );\n}\n"
    }
