      setViewportSize(size);
    };

    updateViewportSize();

    window.addEventListener("resize", updateViewportSize);
    return () => window.removeEventListener("resize", updateViewportSize);
  }, [isVertical]);

  // Handle controlled snap point changes (from external sources like buttons)
  const prevSnapPointRef = React.useRef(activeSnapPointIndex);
  React.useEffect(() => {
    if (prevSnapPointRef.current !== activeSnapPointIndex && open) {
      // Skip if this change came from detection (CSS scroll-snap already handled it)
      if (isStateFromDetectionRef.current) {
        isStateFromDetectionRef.current = false;
        prevSnapPointRef.current = activeSnapPointIndex;
        return;
      }

      // Only scroll if we're not already at the target position
      const container = containerRef.current;
      if (container) {
        const currentScrollPos = isVertical
          ? container.scrollTop
          : container.scrollLeft;
        const targetScrollPos =
          getScrollPositionForSnapPoint(activeSnapPointIndex);
        const tolerance = 10; // pixels

        if (Math.abs(currentScrollPos - targetScrollPos) > tolerance) {
          scrollToSnapPoint(activeSnapPointIndex, "smooth");
        }
      }
    }
    prevSnapPointRef.current = activeSnapPointIndex;
  }, [
    activeSnapPointIndex,
    open,
    scrollToSnapPoint,
    isVertical,
    getScrollPositionForSnapPoint,
  ]);

  // Calculate scroll positions for CSS animation-range
  const firstSnapIndex = dismissible ? 1 : 0;
  const lastSnapIndex = snapScrollPositions.length - 1;
  const firstSnapScrollPos = snapScrollPositions[firstSnapIndex] ?? 0;
  const lastSnapScrollPos = snapScrollPositions[lastSnapIndex] ?? 0;
  const dismissScrollPos = snapScrollPositions[0] ?? 0;

  return {
    containerRef,
    scrollProgress,
    snapProgress,
    isScrolling,
    scrollToSnapPoint,
    scrollToClose,
    snapTargetRefs,
    trackSize,
    snapScrollPositions,
    isInitialized,
    isClosing,
    firstSnapScrollPos,
    lastSnapScrollPos,
    dismissScrollPos,
  };
}

import * as React from "react";

export type DrawerDirection = "top" | "right" | "bottom" | "left";

/**
 * Snap point value:
 * - number (0-1): percentage of drawer visible (0 = closed, 1 = fully open)
 * - `${number}px`: fixed pixel height visible (e.g., "200px")
 */
export type SnapPoint = number | `${number}px`;

/**
 * Parse a pixel value string (e.g., "200px") and return the number
 */
function parsePixelValue(value: string): number | null {
  const match = value.match(/^(\d+(?:\.\d+)?)px$/);
  return match ? parseFloat(match[1]) : null;
}

export interface UseSnapPointsOptions {
  /** Array of snap point values (0-1 or pixel values) */
  snapPoints: SnapPoint[];
  /** Direction the drawer opens from */
  direction: DrawerDirection;
  /** Size of the drawer content (height for vertical, width for horizontal) */
  contentSize: number | null;
  /** Size of the viewport (height for vertical, width for horizontal) */
  viewportSize: number;
}

export interface UseSnapPointsReturn {
  /**
   * Calculated snap positions in pixels
   * Each position represents how far the drawer is translated from its fully-open position
   */
  snapPositions: number[];

  /**
   * Get the snap point index for a given position
   */
  getSnapPointForPosition: (position: number, velocity?: number) => number;

  /**
   * Get the pixel position for a given snap point index
   */
  getPositionForSnapPoint: (snapPointIndex: number) => number;

  /**
   * Get the nearest snap point index based on position and velocity
   */
  getNearestSnapPoint: (
    position: number,
    velocity: number,
    velocityThreshold?: number,
  ) => number;

  /**
   * Check if direction is vertical (top/bottom)
   */
  isVertical: boolean;

  /**
   * Get the maximum drag distance (for progress calculations)
   */
  maxDragDistance: number;
}

/**
 * Hook to manage drawer snap point calculations
 *
 * Snap points represent how much of the drawer is visible:
 * - 0 = fully hidden (dismissed)
 * - 0.5 = half visible
 * - 1 = fully visible
 *
 * The hook converts these to pixel positions based on drawer and viewport sizes.
 */
export function useSnapPoints({
  snapPoints,
  direction,
  contentSize,
  viewportSize,
}: UseSnapPointsOptions): UseSnapPointsReturn {
  const isVertical = direction === "top" || direction === "bottom";

  // Calculate the effective size (use viewport if content size unknown)
  const effectiveSize = contentSize ?? viewportSize;

  // Calculate snap positions in pixels
  // Position 0 = fully open, position = effectiveSize means fully closed
  const snapPositions = React.useMemo(() => {
    return snapPoints.map((snapPoint) => {
      // Handle pixel values (e.g., "200px")
      if (typeof snapPoint === "string") {
        const pixels = parsePixelValue(snapPoint);
        if (pixels !== null) {
          // Pixel value: how many pixels of drawer should be visible
          // position = effectiveSize - pixels (clamped to 0)
          return Math.max(0, effectiveSize - pixels);
        }
        // Invalid string format, treat as fully open
        return 0;
      }

      // Convert snap point (0-1) to pixel position
      // snapPoint 0 = position = effectiveSize (fully hidden)
      // snapPoint 1 = position = 0 (fully visible)
      return effectiveSize * (1 - snapPoint);
    });
  }, [snapPoints, effectiveSize]);

  // Maximum drag distance (from fully open to fully closed)
  const maxDragDistance = effectiveSize;

  // Get position for a specific snap point index
  const getPositionForSnapPoint = React.useCallback(
    (snapPointIndex: number): number => {
      const clampedIndex = Math.max(
        0,
        Math.min(snapPointIndex, snapPositions.length - 1),
      );
      return snapPositions[clampedIndex];
    },
    [snapPositions],
  );

  // Get the snap point index closest to a given position
  const getSnapPointForPosition = React.useCallback(
    (position: number): number => {
      let closestIndex = 0;
      let closestDistance = Math.abs(position - snapPositions[0]);

      for (let i = 1; i < snapPositions.length; i++) {
        const distance = Math.abs(position - snapPositions[i]);
        if (distance < closestDistance) {
          closestDistance = distance;
          closestIndex = i;
        }
      }

      return closestIndex;
    },
    [snapPositions],
  );

  // Get nearest snap point considering velocity
  const getNearestSnapPoint = React.useCallback(
    (
      position: number,
      velocity: number,
      velocityThreshold: number = 0.5,
    ): number => {
      // If velocity is significant, bias towards the direction of movement
      if (Math.abs(velocity) > velocityThreshold) {
        // For bottom/right drawers: positive velocity = closing, negative = opening
        // For top/left drawers: negative velocity = closing, positive = opening
        const isClosingDirection =
          direction === "bottom" || direction === "right"
            ? velocity > 0
            : velocity < 0;

        // Find snap points in the direction of movement
        const currentSnapPoint = getSnapPointForPosition(position);
        const currentPosition = snapPositions[currentSnapPoint];

        if (isClosingDirection) {
          // Moving towards closed - find next snap point with larger position value
          for (let i = 0; i < snapPositions.length; i++) {
            if (snapPositions[i] > currentPosition) {
              return i;
            }
          }
          // Already at most closed position
          return snapPositions.length - 1;
        } else {
          // Moving towards open - find next snap point with smaller position value
          for (let i = snapPositions.length - 1; i >= 0; i--) {
            if (snapPositions[i] < currentPosition) {
              return i;
            }
          }
          // Already at most open position
          return 0;
        }
      }

      // Low velocity - snap to nearest snap point
      return getSnapPointForPosition(position);
    },
    [direction, snapPositions, getSnapPointForPosition],
  );

  return {
    snapPositions,
    getSnapPointForPosition,
    getPositionForSnapPoint,
    getNearestSnapPoint,
    isVertical,
    maxDragDistance,
  };
}

import * as React from "react";

export interface UseVirtualKeyboardOptions {
  /** Whether keyboard handling is enabled */
  enabled?: boolean;
}

export interface UseVirtualKeyboardReturn {
  /** Current keyboard height in pixels (0 when closed) */
  keyboardHeight: number;
  /** Whether the virtual keyboard is currently visible */
  isKeyboardVisible: boolean;
}

/**
 * Hook to detect virtual keyboard visibility and height using the Visual Viewport API.
 *
 * On mobile devices, when a virtual keyboard appears, it reduces the visual viewport height.
 * This hook tracks that change so components can adjust their layout accordingly.
 */
export function useVirtualKeyboard({
  enabled = true,
}: UseVirtualKeyboardOptions = {}): UseVirtualKeyboardReturn {
  const [keyboardHeight, setKeyboardHeight] = React.useState(0);
  const [isKeyboardVisible, setIsKeyboardVisible] = React.useState(false);

  React.useEffect(() => {
    if (!enabled || typeof window === "undefined") return;

    const visualViewport = window.visualViewport;
    if (!visualViewport) return;

    // Store initial viewport height to compare against
    let initialHeight = visualViewport.height;

    const handleResize = () => {
      // The keyboard height is the difference between the initial viewport height
      // and the current viewport height
      const currentHeight = visualViewport.height;
      const heightDiff = initialHeight - currentHeight;

      // Only consider it a keyboard if the difference is significant (> 100px)
      // This avoids false positives from address bar hiding, etc.
      const isKeyboard = heightDiff > 100;

      setKeyboardHeight(isKeyboard ? heightDiff : 0);
      setIsKeyboardVisible(isKeyboard);
    };

    // Also handle scroll to keep track when keyboard scrolls content
    const handleScroll = () => {
      // Re-check on scroll as the viewport may have changed
      handleResize();
    };

    // Update initial height on orientation change
    const handleOrientationChange = () => {
      // Wait a bit for the viewport to settle
      setTimeout(() => {
        initialHeight = visualViewport.height;
        handleResize();
      }, 100);
    };

    visualViewport.addEventListener("resize", handleResize);
    visualViewport.addEventListener("scroll", handleScroll);
    window.addEventListener("orientationchange", handleOrientationChange);

    // Initial check
    handleResize();

    return () => {
      visualViewport.removeEventListener("resize", handleResize);
      visualViewport.removeEventListener("scroll", handleScroll);
      window.removeEventListener("orientationchange", handleOrientationChange);
    };
  }, [enabled]);

  return {
    keyboardHeight,
    isKeyboardVisible,
  };
}

/* -------------------------------------------------------------------------------------------------
 * Drawer Scroll-Driven Animations (Progressive Enhancement)
 * Chrome 115+, Safari 26+ (future), Firefox flag-only
 * -------------------------------------------------------------------------------------------------*/

/**
 * CSS Custom Property for scroll-driven drawer animations
 * Allows consumers to animate content based on snap progress:
 * .mini-player { opacity: calc(1 - var(--drawer-snap-progress, 0)); }
 * .full-player { opacity: var(--drawer-snap-progress, 0); }
 */
@property --drawer-snap-progress {
  syntax: "<number>";
  inherits: true;
  initial-value: 0;
}

/**
 * Animates --drawer-snap-progress from 0 to 1 based on scroll position
 * Applied to drawer viewport with animation-timeline: scroll()
 */
@keyframes drawer-snap-progress {
  from {
    --drawer-snap-progress: 0;
  }
  to {
    --drawer-snap-progress: 1;
  }
}

/**
 * Animates backdrop opacity based on drawer panel visibility (view timeline)
 * Uses view() timeline - opacity naturally scales with how much drawer is visible
 * Applied to drawer backdrop with animation-timeline: --drawer-panel
 */
@keyframes drawer-backdrop-fade {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

/**
 * Exit animation for drawer backdrop
 * Animates from current opacity to 0 (no 'from' = starts from computed value)
 * Used to override scroll-driven animations which transitions can't interpolate from
 */
@keyframes drawer-backdrop-exit {
  to {
    opacity: 0;
  }
}
