        : container.scrollWidth - container.clientWidth;
      if (isVertical) {
        container.scrollTop = maxScroll;
      } else {
        container.scrollLeft = maxScroll;
      }
    }

    // Wait for contentSize to be measured before precise positioning
    if (contentSize === null) return;

    // Perform initial scroll positioning (only once per open)
    if (!initRef.current.hasInitialized) {
      const performInitialScroll = () => {
        if (!container) {
          initRef.current.retryTimeout = setTimeout(performInitialScroll, 0);
          return;
        }

        const size = isVertical
          ? container.clientHeight
          : container.clientWidth;
        if (size === 0) {
          initRef.current.retryTimeout = setTimeout(performInitialScroll, 0);
          return;
        }

        initRef.current.hasInitialized = true;

        if (size !== viewportSize) {
          setViewportSize(size);
        }

        // Calculate target scroll position
        const targetIndex = dismissible
          ? activeSnapPointIndex + 1
          : activeSnapPointIndex;
        const targetScrollPos = snapScrollPositions[targetIndex] ?? 0;

        // Mark as programmatic scroll
        scrollControlRef.current.isProgrammatic = true;
        scrollControlRef.current.lastDetectedSnapIndex = activeSnapPointIndex;

        // Set initial scroll position
        // scroll-snap is disabled via React state until setIsInitialized(true)
        if (isVertical) {
          container.scrollTop = targetScrollPos;
        } else {
          container.scrollLeft = targetScrollPos;
        }

        // Calculate and emit initial progress via callbacks
        const initialProgress = calculateScrollProgress(
          targetScrollPos,
          geometry,
        );
        onScrollProgress?.(initialProgress);

        const initialSnapProgress = calculateSnapProgress(
          targetScrollPos,
          snapScrollPositions,
          dismissible,
        );
        onSnapProgress?.(initialSnapProgress);

        // Clear programmatic flag after a tick
        setTimeout(() => {
          scrollControlRef.current.isProgrammatic = false;
        }, 0);

        setIsInitialized(true);
      };

      // Try synchronous first, fall back to setTimeout if needed
      performInitialScroll();
    }

    // Capture ref value for cleanup (avoids stale ref warning)
    const currentInit = initRef.current;
    return () => {
      if (currentInit.retryTimeout) {
        clearTimeout(currentInit.retryTimeout);
        currentInit.retryTimeout = null;
      }
    };
  }, [
    open,
    contentSize,
    isVertical,
    isInverted,
    viewportSize,
    dismissible,
    activeSnapPointIndex,
    snapScrollPositions,
    geometry,
    onScrollProgress,
    onSnapProgress,
  ]);

  // Effect 2: Keep lastDetectedSnapIndex in sync with activeSnapPointIndex
  // This prevents handleScrollSnapChange from setting isFromDetection=true after programmatic scrolls
  React.useEffect(() => {
    if (open) {
      scrollControlRef.current.lastDetectedSnapIndex = activeSnapPointIndex;
    }
  }, [open, activeSnapPointIndex]);

  // Effect 3: Handle controlled snap point changes (from external sources like buttons)
  const prevSnapPointRef = React.useRef(activeSnapPointIndex);
  React.useEffect(() => {
    if (prevSnapPointRef.current !== activeSnapPointIndex && open) {
      // Skip if this change came from detection (CSS scroll-snap already handled it)
      if (scrollControlRef.current.isFromDetection) {
        scrollControlRef.current.isFromDetection = false;
        prevSnapPointRef.current = activeSnapPointIndex;
        return;
      }

      // Only scroll if we're not already at the target position
      const container = containerRef.current;
      if (container) {
        const currentScrollPos = isVertical
          ? container.scrollTop
          : container.scrollLeft;
        const targetScrollPos =
          getScrollPositionForSnapPoint(activeSnapPointIndex);
        const tolerance = 10; // pixels

        if (Math.abs(currentScrollPos - targetScrollPos) > tolerance) {
          scrollToSnapPoint(activeSnapPointIndex, "smooth");
        }
      }
    }
    prevSnapPointRef.current = activeSnapPointIndex;
  }, [
    activeSnapPointIndex,
    open,
    scrollToSnapPoint,
    isVertical,
    getScrollPositionForSnapPoint,
  ]);

  // Effect 4: Re-position when geometry changes (e.g., keyboard appears on Android)
  // This ensures the drawer stays at the correct snap point when viewport/content size changes
  const prevGeometryRef = React.useRef({ trackSize: 0, maxScroll: 0 });
  React.useEffect(() => {
    if (!open || !initRef.current.hasInitialized) return;

    const { trackSize, maxScroll } = geometry;
    const prev = prevGeometryRef.current;

    // Check if geometry actually changed (not just first render after init)
    if (prev.trackSize !== 0 && prev.trackSize !== trackSize) {
      // Geometry changed - re-scroll to current snap point to maintain position
      const container = containerRef.current;
      if (container && !scrollControlRef.current.isProgrammatic) {
        const targetScrollPos =
          getScrollPositionForSnapPoint(activeSnapPointIndex);
        const prevScrollBehavior = container.style.scrollBehavior;

        // Use instant scroll to avoid visible animation
        container.style.scrollBehavior = "auto";
        if (isVertical) {
          container.scrollTop = targetScrollPos;
        } else {
          container.scrollLeft = targetScrollPos;
        }
        container.style.scrollBehavior = prevScrollBehavior;
      }
    }

    prevGeometryRef.current = { trackSize, maxScroll };
  }, [
    open,
    geometry,
    isVertical,
    activeSnapPointIndex,
    getScrollPositionForSnapPoint,
  ]);

  // Effect 5: Event listeners (stable handlers, minimal dependencies)
  React.useEffect(() => {
    const container = containerRef.current;
    if (!container || !open) return;

    // Attach event listeners
    container.addEventListener("scroll", handleScroll, { passive: true });

    // Touch tracking to distinguish "holding still" from "released with momentum"
    // Note: We use touch events only (not mouse) to avoid false positives from
    // mouse click jitter on desktop triggering scroll events
    container.addEventListener("touchstart", handleTouchStart, {
      passive: true,
    });
    container.addEventListener("touchend", handleTouchEnd, { passive: true });
    container.addEventListener("touchcancel", handleTouchEnd, {
      passive: true,
    });

    if (supportsScrollEnd) {
      container.addEventListener("scrollend", handleScrollEnd);
    }

    if (supportsScrollSnapChange) {
      container.addEventListener("scrollsnapchange", handleScrollSnapChange);
    }

    // Viewport resize handling
    const updateViewportSize = () => {
      const size = isVertical ? container.clientHeight : container.clientWidth;
      setViewportSize(size);
    };
    window.addEventListener("resize", updateViewportSize);

    return () => {
      container.removeEventListener("scroll", handleScroll);
      container.removeEventListener("touchstart", handleTouchStart);
      container.removeEventListener("touchend", handleTouchEnd);
      container.removeEventListener("touchcancel", handleTouchEnd);

      if (supportsScrollEnd) {
        container.removeEventListener("scrollend", handleScrollEnd);
      }

      if (supportsScrollSnapChange) {
        container.removeEventListener(
          "scrollsnapchange",
          handleScrollSnapChange,
        );
      }

      window.removeEventListener("resize", updateViewportSize);
    };
  }, [
    open,
    isVertical,
    handleScroll,
    handleScrollEnd,
    handleScrollSnapChange,
    handleTouchStart,
    handleTouchEnd,
  ]);

  return {
    containerRef,
    isScrolling,
    snapTargetRefs,
    trackSize: geometry.trackSize,
    snapScrollPositions,
    isInitialized,
    isClosing,
    firstSnapScrollPos,
    lastSnapScrollPos,
  };
}

import * as React from "react";

export interface UseVirtualKeyboardOptions {
  /** Whether keyboard handling is enabled */
  enabled?: boolean;
}

export interface UseVirtualKeyboardReturn {
  /** Current keyboard height in pixels (0 when closed) */
  keyboardHeight: number;
  /** Whether the virtual keyboard is currently visible */
  isKeyboardVisible: boolean;
}

/**
 * Helper to check if element is an input that would trigger a virtual keyboard.
 */
function isInput(element: HTMLElement | null): boolean {
  if (!element) return false;
  const tagName = element.tagName.toLowerCase();
  return (
    tagName === "input" ||
    tagName === "textarea" ||
    tagName === "select" ||
    element.isContentEditable
  );
}

/**
 * Detect Firefox mobile which natively handles keyboard repositioning.
 * We skip our custom transform on Firefox to avoid double-repositioning.
 */
const isFirefoxMobile =
  typeof navigator !== "undefined" &&
  /Firefox/i.test(navigator.userAgent) &&
  /Android|Mobile/i.test(navigator.userAgent);

/**
 * Hook to detect virtual keyboard visibility and height using the Visual Viewport API.
 *
 * On mobile devices, when a virtual keyboard appears, it reduces the visual viewport height.
 * This hook tracks that change so components can adjust their layout accordingly.
 *
 * Simplified approach:
 * - Keyboard is visible if viewport is significantly smaller (>100px) AND an input is focused
 * - No complex toggle state that can get out of sync
 */
export function useVirtualKeyboard({
  enabled = true,
}: UseVirtualKeyboardOptions = {}): UseVirtualKeyboardReturn {
  const [keyboardHeight, setKeyboardHeight] = React.useState(0);

  React.useEffect(() => {
    // Firefox mobile natively handles keyboard repositioning, so we skip
    // our custom detection to avoid double-repositioning the drawer
    if (!enabled || typeof window === "undefined" || isFirefoxMobile) return;

    // Enable Virtual Keyboard API to get env(keyboard-inset-*) CSS variables
    // This tells the browser that we'll handle the virtual keyboard geometry ourselves
    if ("virtualKeyboard" in navigator) {
      (navigator as Navigator & { virtualKeyboard: { overlaysContent: boolean } })
        .virtualKeyboard.overlaysContent = true;
    }

    const visualViewport = window.visualViewport;
    if (!visualViewport) return;

    const handleResize = () => {
      const focusedElement = document.activeElement as HTMLElement;
      const isInputFocused = isInput(focusedElement);

      // If no input is focused, keyboard can't be open for our purposes
      if (!isInputFocused) {
        setKeyboardHeight(0);
        return;
      }

      const visualViewportHeight = visualViewport.height;
      const totalHeight = window.innerHeight;
      const diff = totalHeight - visualViewportHeight;

      // Keyboard is visible if viewport is significantly smaller (>100px threshold)
      // This avoids false positives from address bar changes
      if (diff > 100) {
        setKeyboardHeight(diff);
      } else {
        setKeyboardHeight(0);
      }
    };

    visualViewport.addEventListener("resize", handleResize);

    // Initial check in case keyboard is already open
    handleResize();

    return () => visualViewport.removeEventListener("resize", handleResize);
  }, [enabled]);

  return {
    keyboardHeight,
    isKeyboardVisible: keyboardHeight > 0,
  };
}

import * as React from "react";

export interface UseVisualViewportHeightOptions {
  /** Whether tracking is enabled */
  enabled?: boolean;
}

/**
 * Hook to track the visual viewport height using the Visual Viewport API.
 *
 * On mobile devices, the visual viewport height changes when the URL bar
 * collapses/expands. This hook provides real-time tracking of the actual
 * visible viewport height, which can be used to size elements that need
 * to fill the visible area regardless of URL bar state.
 *
 * @returns The current visual viewport height in pixels, or null if not available
 */
export function useVisualViewportHeight({
  enabled = true,
}: UseVisualViewportHeightOptions = {}): number | null {
  const [height, setHeight] = React.useState<number | null>(() => {
    if (typeof window === "undefined") return null;
    return window.visualViewport?.height ?? null;
  });

  React.useEffect(() => {
    if (!enabled || typeof window === "undefined") return;

    const visualViewport = window.visualViewport;
    if (!visualViewport) return;

    const handleResize = () => {
      setHeight(visualViewport.height);
    };

    // Set initial value
    handleResize();

    visualViewport.addEventListener("resize", handleResize);
    return () => visualViewport.removeEventListener("resize", handleResize);
  }, [enabled]);

  return height;
}

/* -------------------------------------------------------------------------------------------------
 * Drawer Scroll-Driven Animations (Progressive Enhancement)
 * Chrome 115+, Safari 26+ (future), Firefox flag-only
 * -------------------------------------------------------------------------------------------------*/

/**
 * CSS Custom Property for scroll-driven drawer animations
 * Allows consumers to animate content based on snap progress:
 * .mini-player { opacity: calc(1 - var(--drawer-snap-progress, 0)); }
 * .full-player { opacity: var(--drawer-snap-progress, 0); }
 */
@property --drawer-snap-progress {
  syntax: "<number>";
  inherits: true;
  initial-value: 0;
}

/**
 * Animates --drawer-snap-progress from 0 to 1 based on scroll position
 * Applied to drawer viewport with animation-timeline: scroll()
 */
@keyframes drawer-snap-progress {
  from {
    --drawer-snap-progress: 0;
  }
  to {
    --drawer-snap-progress: 1;
  }
}

/**
 * Animates backdrop opacity based on drawer panel visibility (view timeline)
 * Uses view() timeline - opacity naturally scales with how much drawer is visible
 * Applied to drawer backdrop with animation-timeline: --drawer-panel
 */
@keyframes drawer-backdrop-fade {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

/**
 * Exit animation for drawer backdrop
 * Animates from current opacity to 0 (no 'from' = starts from computed value)
 * Used to override scroll-driven animations which transitions can't interpolate from
 */
@keyframes drawer-backdrop-exit {
  to {
    opacity: 0;
  }
}

html:has([data-slot="drawer-viewport"][data-direction="left"]),
html:has([data-slot="drawer-viewport"][data-direction="right"]) {
  overscroll-behavior: none;
  scrollbar-width: none;
}

html:has(
    [data-slot="drawer-viewport"][data-direction="left"]
  )::-webkit-scrollbar,
html:has(
    [data-slot="drawer-viewport"][data-direction="right"]
  )::-webkit-scrollbar {
  display: none !important;
}
