      const progress = calculateProgress(scrollPos);
      setScrollProgress(progress);
      onScrollProgress?.(progress);

      // Calculate and emit snap progress
      const snapProg = calculateSnapProgress(scrollPos);
      setSnapProgress(snapProg);
